# Natural Language Toolkitimport nltkimport numpy as np#import matplotlib.pyplot as plt# Import data file to carry on Data Analystimport DataAnalysis as da# use of a Data Set from UCI --> SMS Spam Collection Data Set# Data set with messages label as Spam of Ham (good ones)def DataPreprocessor(file_name):    #open the file of the Messages    f = open(file_name)    # we get the messages into a list variable,we split the text by the endsapce of each message    messages = [line.rstrip() for line in f] # spaces at the end of the string are removed    return messagesdef main():    messages = DataPreprocessor('SMSSpamCollection')    #da.PrintNMessages(messages, 24)    messages_df = da.CreateDataFrame('SMSSpamCollection')    da.GetNFirstElementsDataFrame(messages_df, 8)    da.DescribeDataFrame(messages_df)    da.DescribeByLabel(messages_df, 'label')    # we see that we have way more "ham" messages than "spam" messages    #da.AnalyzeLengthMessages(messages)if __name__ == "__main__":    main()