# Natural Language Toolkitimport nltk# use of a Data Set from UCI --> SMS Spam Collection Data Set# Data set with messages label as Spam of Ham (good ones)def main():    print('Hello World')    #open the file of the Messages    f = open('SMSSpamCollection')    # we get the messages into a list variable,we split the text by the endsapce of each message    messages = [line.rstrip() for line in f] # spaces at the end of the strin are removed    print(len(messages))    print(messages[0]) # first message ( first element of the list )    print(type(messages))if __name__ == "__main__":    main()